// 原始CDN URL
const ORIGINAL_WASM_URL = 'https://makemaze.online/wasm/1750021714445_8u87qbny.wasm';
const ORIGINAL_JS_URL = 'https://makemaze.online/js/1750023870567_wstc5r0x.js';

// 通过API代理加载WASM模块
const WASM_CDN_URL = `/api/wasm-proxy?url=${encodeURIComponent(ORIGINAL_WASM_URL)}`;
const WASM_CDN_JS_URL = `/api/wasm-proxy?url=${encodeURIComponent(ORIGINAL_JS_URL)}`;

// 本地路径作为备选
const LOCAL_WASM_JS_URL = '../pkg/uesave_wasm.js';

// localStorage存储键值
const STORAGE_KEYS = {
 WASM_BINARY: 'cached_wasm_binary',
 WASM_JS: 'cached_wasm_js',
 TIMESTAMP: 'wasm_cache_timestamp',
};

// 缓存过期时间：1个月（毫秒）
const CACHE_EXPIRATION = 30 * 24 * 60 * 60 * 1000;

// 模拟未加载状态下的函数接口
let parse_sav_to_json = null;
let encode_json_to_sav = null;
let init = null;

// 全局状态
let wasmInitialized = false;
let initPromise = null;
let loadAttempts = 0;

/**
 * 检测浏览器环境和功能
 */
function detectEnvironment() {
 try {
 const env = {};

 // 检查浏览器信息
 if (typeof navigator !== 'undefined') {
 env.userAgent = navigator.userAgent || 'unknown';
 env.language = navigator.language || 'unknown';
 }

 // 检查WebAssembly支持
 env.webAssemblySupport = false;
 if (typeof WebAssembly !== 'undefined') {
 env.webAssemblySupport = true;
 }

 // 检查fetch支持
 env.fetchSupport = false;
 if (typeof fetch !== 'undefined') {
 env.fetchSupport = true;
 }

 // 检查localStorage支持
 env.localStorageSupport = false;
 if (typeof localStorage !== 'undefined') {
 try {
 localStorage.setItem('__test', '1');
 localStorage.removeItem('__test');
 env.localStorageSupport = true;
 } catch (e) {
 // localStorage可能被禁用或处于隐私模式
 }
 }

 // 检查cookie支持
 env.cookieSupport = false;
 if (typeof document !== 'undefined' && document.cookie) {
 env.cookieSupport = true;
 env.secureCookies = document.cookie.includes('secure');
 }

 // 尝试检测隐私模式
 env.isPrivateMode = false;
 try {
 if (typeof localStorage !== 'undefined') {
 localStorage.setItem('__test', '1');
 localStorage.removeItem('__test');
 }
 } catch (e) {
 env.isPrivateMode = true;
 }

 console.log('Browser environment:', env);
 return env;
 } catch (e) {
 console.error('Error detecting environment:', e);
 return { error: e.message };
 }
}

/**
 * 检查localStorage缓存是否过期
 */
function isCacheExpired() {
 try {
 const timestamp = localStorage.getItem(STORAGE_KEYS.TIMESTAMP);
 if (!timestamp) return true;

 const cacheTime = parseInt(timestamp, 10);
 const currentTime = Date.now();

 return (currentTime - cacheTime) > CACHE_EXPIRATION;
 } catch (e) {
 console.warn('Error checking cache expiration:', e);
 return true; // 如果出错，视为缓存过期
 }
}

/**
 * 保存WASM到localStorage
 */
async function saveToLocalStorage(wasmBinary, jsText) {
 try {
 // 保存WASM二进制数据
 if (wasmBinary) {
 // 转换Uint8Array为Base64
 const binary = Array.from(wasmBinary)
 .map(byte => String.fromCharCode(byte))
 .join('');
 const base64 = btoa(binary);
 localStorage.setItem(STORAGE_KEYS.WASM_BINARY, base64);
 }

 // 保存JS模块
 if (jsText) {
 localStorage.setItem(STORAGE_KEYS.WASM_JS, jsText);
 }

 // 保存当前时间戳
 localStorage.setItem(STORAGE_KEYS.TIMESTAMP, Date.now().toString());

 console.log('✅ WASM module saved to localStorage cache');
 return true;
 } catch (e) {
 console.warn('Failed to save WASM to localStorage:', e);
 return false;
 }
}

/**
 * 初始化 WASM 模块
 * 使用单例模式确保只初始化一次
 */
export const initializeWasm = async () => {
 // 如果已经初始化，直接返回
 if (wasmInitialized) {
 return;
 }

 // 如果正在初始化，等待完成
 if (initPromise) {
 return initPromise;
 }

 // 开始初始化
 initPromise = (async () => {
 try {
 console.log(" Initializing WASM module...");

 // 检测环境
 const env = detectEnvironment();

 // 最大尝试次数
 const maxAttempts = 3;
 loadAttempts++;

 if (loadAttempts > maxAttempts) {
 throw new Error(`Maximum attempts reached (${maxAttempts}), please refresh the page`);
 }

 // 尝试不同的加载方法，直到成功为止
 const loadMethods = [
 loadFromLocalStorage, // 首先尝试从localStorage加载
 loadFromCdnImport, 
 loadFromCdnFetch, 
 loadFromLocalImport,
 // setupFallbackFunctions
 ];

 let lastError = null;

 // 尝试所有加载方法
 for (const loadMethod of loadMethods) {
 try {
 console.log(`Trying to load using ${loadMethod.name} method...`);
 await loadMethod();
 wasmInitialized = true;
 console.log(`✅ WASM module initialized successfully via ${loadMethod.name}`);
 return;
 } catch (error) {
 lastError = error;
 console.warn(`❌ ${loadMethod.name} method failed:`, error);
 // 继续尝试下一个方法
 }
 }

 // 如果所有方法都失败
 throw new Error(`All loading methods failed, last error: ${lastError}`);
 } catch (error) {
 console.error("❌ WASM module initialization failed:", error);
 wasmInitialized = false;
 initPromise = null;
 throw new Error(`WASM initialization failed: ${error}`);
 }
 })();

 return initPromise;
};

// 方法0: 从localStorage加载缓存的WASM
async function loadFromLocalStorage() {
 try {
 // 检查环境是否支持localStorage
 const env = detectEnvironment();
 if (!env.localStorageSupport) {
 throw new Error('Browser does not support localStorage');
 }

 // 检查缓存是否过期
 if (isCacheExpired()) {
 throw new Error('WASM cache has expired');
 }

 // 从localStorage获取WASM模块
 const jsText = localStorage.getItem(STORAGE_KEYS.WASM_JS);
 const wasmBase64 = localStorage.getItem(STORAGE_KEYS.WASM_BINARY);

 if (!jsText || !wasmBase64) {
 throw new Error('No WASM data in cache');
 }

 console.log("Loading WASM module from localStorage...");

 // 将JS文本转换为模块
 const wasmModule = new Function('return ' + jsText)();

 init = wasmModule.default;
 parse_sav_to_json = wasmModule.parse_sav_to_json;
 encode_json_to_sav = wasmModule.encode_json_to_sav;

 // 将Base64转换回二进制
 const binaryString = atob(wasmBase64);
 const len = binaryString.length;
 const bytes = new Uint8Array(len);
 for (let i = 0; i < len; i++) {
 bytes[i] = binaryString.charCodeAt(i);
 }

 // 创建Blob和URL
 const blob = new Blob([bytes], { type: 'application/wasm' });
 const wasmUrl = URL.createObjectURL(blob);

 // 初始化WASM模块
 await init(wasmUrl);

 // 用完后释放URL
 URL.revokeObjectURL(wasmUrl);

 return true;
 } catch (error) {
 console.warn("Failed to load WASM from localStorage:", error);
 throw error;
 }
}

// 方法1: 通过动态导入CDN JS模块
async function loadFromCdnImport() {
 try {
 const wasmModule = await import(/* webpackIgnore: true */ WASM_CDN_JS_URL);
 init = wasmModule.default;
 parse_sav_to_json = wasmModule.parse_sav_to_json;
 encode_json_to_sav = wasmModule.encode_json_to_sav;

 console.log("JS module loaded successfully, initializing WASM...");

 // 初始化WASM模块
 await init(WASM_CDN_URL);

 // 异步获取JS和WASM并缓存到localStorage
 try {
 // 获取JS模块文本
 const jsResponse = await fetch(WASM_CDN_JS_URL);
 const jsText = await jsResponse.text();

 // 获取WASM二进制
 const wasmResponse = await fetch(WASM_CDN_URL);
 const wasmBinary = new Uint8Array(await wasmResponse.arrayBuffer());

 // 保存到localStorage
 await saveToLocalStorage(wasmBinary, jsText);
 } catch (cacheError) {
 console.warn("Failed to cache WASM to localStorage:", cacheError);
 // 继续执行，不影响主流程
 }
 } catch (error) {
 console.error("Failed to load module via import:", error);
 throw error;
 }
}

// 方法2: 通过fetch加载CDN JS模块
async function loadFromCdnFetch() {
 const fetchOptions = {
 credentials: 'omit', // 不发送cookie
 mode: 'cors',
 cache: 'no-cache', // 不使用缓存
 };

 // 先加载JS
 const jsResponse = await fetch(WASM_CDN_JS_URL, fetchOptions);
 if (!jsResponse.ok) {
 throw new Error(`Failed to load WASM JS file: ${jsResponse.status} ${jsResponse.statusText}`);
 }

 // 获取JS文本
 const jsText = await jsResponse.text();

 // 评估JS模块
 const wasmModule = new Function('return ' + jsText)();

 init = wasmModule.default;
 parse_sav_to_json = wasmModule.parse_sav_to_json;
 encode_json_to_sav = wasmModule.encode_json_to_sav;

 // 加载WASM二进制
 const wasmResponse = await fetch(WASM_CDN_URL, fetchOptions);
 if (!wasmResponse.ok) {
 throw new Error(`Failed to load WASM binary: ${wasmResponse.status} ${wasmResponse.statusText}`);
 }

 const wasmBinary = new Uint8Array(await wasmResponse.arrayBuffer());
 await init(WASM_CDN_URL);

 // 保存到localStorage
 try {
 await saveToLocalStorage(wasmBinary, jsText);
 } catch (cacheError) {
 console.warn("Failed to cache WASM to localStorage:", cacheError);
 // 继续执行，不影响主流程
 }
}

// 方法3: 从本地加载
async function loadFromLocalImport() {
 try {
 const wasmModule = await import(LOCAL_WASM_JS_URL);
 init = wasmModule.default;
 parse_sav_to_json = wasmModule.parse_sav_to_json;
 encode_json_to_sav = wasmModule.encode_json_to_sav;

 await init();
 } catch (error) {
 console.error("Failed to load module locally:", error);
 throw error;
 }
}

// 方法4: 设置后备函数（仅提供UI功能，无实际处理能力）
async function setupFallbackFunctions() {
 console.warn("Using fallback function mode - UI only, no actual data processing");

 // 创建模拟函数，返回固定数据
 parse_sav_to_json = (data) => {
 console.warn("Using mock function parse_sav_to_json");
 return JSON.stringify({
 root: {
 properties: {
 'Gold_0': { Int: 1000 },
 'InventoryItems_0': {
 Map: [
 { key: { Name: "sample_item_1" }, value: { Int: 1 } },
 { key: { Name: "sample_item_2" }, value: { Int: 1 } }
 ]
 }
 }
 }
 });
 };

 encode_json_to_sav = (jsonStr) => {
 console.warn("Using mock function encode_json_to_sav");
 // 返回一个空的Uint8Array作为模拟数据
 return new Uint8Array(10);
 };

 // 模拟init函数
 init = async () => {
 console.warn("Using mock initialization function");
 return true;
 };

 // 显示警告弹窗
 setTimeout(() => {
 alert("Note: WASM module failed to load, currently in limited functionality mode. Save function is not available.");
 }, 1000);

 return true;
}

/**
 * 确保 WASM 已初始化
 */
const ensureWasmReady = async () => {
 await initializeWasm();

 if (!wasmInitialized) {
 throw new Error('WASM module not properly initialized');
 }
};

/**
 * 清除WASM缓存
 */
export const clearWasmCache = () => {
 try {
 localStorage.removeItem(STORAGE_KEYS.WASM_BINARY);
 localStorage.removeItem(STORAGE_KEYS.WASM_JS);
 localStorage.removeItem(STORAGE_KEYS.TIMESTAMP);
 console.log('✅ WASM cache cleared');
 return true;
 } catch (e) {
 console.warn('Failed to clear WASM cache:', e);
 return false;
 }
};

/**
 * 解析 SAV 文件为 JSON 对象
 */
export const parseSavFile = async (data) => {
 // 输入验证
 if (!(data instanceof Uint8Array)) {
 throw new Error('Input data must be Uint8Array type');
 }

 if (data.length === 0) {
 throw new Error('Input data is empty');
 }

 console.log(` Starting to parse SAV file, size: ${data.length} bytes`);

 try {
