 6	export default function LanguageSwitcher() {
 7	 const router = useRouter();
 8	 const [locale, setLocale] = useState('zh');
 9	 const t = translations[locale] || translations.zh;
 10	
 11	 useEffect(() => {
 12	 // 从 localStorage 获取语言设置，或者根据浏览器语言设置默认值
 13	 const savedLocale = localStorage.getItem('locale');
 14	 if (savedLocale && (savedLocale === 'zh' || savedLocale === 'en')) {
 15	 setLocale(savedLocale);
 16	 } else {
 17	 // 检测浏览器语言
 18	 const browserLang = navigator.language.toLowerCase();
 19	 const detectedLocale = browserLang.startsWith('zh') ? 'zh' : 'en';
 20	 setLocale(detectedLocale);
 21	 localStorage.setItem('locale', detectedLocale);
 22	 }
 23	 }, []);
 24	
 25	 const switchLanguage = () => {
 26	 const newLocale = locale === 'zh' ? 'en' : 'zh';
 27	 setLocale(newLocale);
 28	 localStorage.setItem('locale', newLocale);
 29	 
 30	 // 触发自定义事件通知其他组件语言已更改
 31	 window.dispatchEvent(new CustomEvent('localeChange', { detail: newLocale }));
 32	 };
 33	
 34	 return (
 35	 <Box position="fixed" top="4" right="4" zIndex="1000">
 36	 <Button
 37	 onClick={switchLanguage}
 38	 colorScheme="blue"
 39	 variant="outline"
 40	 size="sm"
 41	 bg="white"
 42	 _hover={{ bg: "blue.50" }}
 43	 aria-label={locale === 'en' ? '切换到中文' : 'Switch to English'}
