Path: lib/useLocale.js
 1	import { useState, useEffect } from 'react';
 2	import { translations } from './translations';
 3	
 4	export function useLocale() {
 5	 const [locale, setLocale] = useState('zh');
 6	
 7	 useEffect(() => {
 8	 // 从 localStorage 获取语言设置
 9	 const savedLocale = localStorage.getItem('locale');
 10	 if (savedLocale && (savedLocale === 'zh' || savedLocale === 'en')) {
 11	 setLocale(savedLocale);
 12	 } else {
 13	 // 检测浏览器语言
 14	 const browserLang = navigator.language.toLowerCase();
 15	 const detectedLocale = browserLang.startsWith('zh') ? 'zh' : 'en';
 16	 setLocale(detectedLocale);
 17	 localStorage.setItem('locale', detectedLocale);
 18	 }
 19	
 20	 // 监听语言变更事件
 21	 const handleLocaleChange = (event) => {
 22	 setLocale(event.detail);
 23	 };
 24	
 25	 window.addEventListener('localeChange', handleLocaleChange);
 26	 return () => window.removeEventListener('localeChange', handleLocaleChange);
 27	 }, []);
 28	
 29	 const t = translations[locale] || translations.zh;
 30	
 31	 return { locale, t };
 32	} ...
