 1	import { ChakraProvider, Alert, AlertIcon, AlertTitle, AlertDescription, Link, Box } from '@chakra-ui/react';
 2	import Head from 'next/head';
 3	import { useEffect } from 'react';
 4	import LanguageSwitcher from '../components/LanguageSwitcher';
 5	import Navigation from '../components/Navigation';
 6	import ThirdPartyScripts from '../components/ThirdPartyScripts';
 7	import PerformanceMonitor, { runWhenIdle, shouldDelayLoad } from '../lib/performance';
 8	import { inject } from "@vercel/analytics"
 9	// 主要 CSS 文件保持同步加载（关键样式）
 10	import './editor.css';
 11	
 12	export default function App({ Component, pageProps }) {
 13	 inject()
 14	 // 性能监控和延迟加载优化
 15	 useEffect(() => {
 16	 // 启动性能监控
 17	 const performanceMonitor = new PerformanceMonitor();
 18	 performanceMonitor.startMonitoring();
 19	 
 20	 // 延迟加载非关键资源
 21	 const loadNonCriticalResources = () => {
 22	 // 检查是否应该延迟加载（基于网络条件）
 23	 const shouldDelay = shouldDelayLoad();
 24	 
 25	 const loadCSS = (href, delay = 0) => {
 26	 setTimeout(() => {
 27	 const link = document.createElement('link');
 28	 link.rel = 'stylesheet';
 29	 link.href = href;
 30	 link.media = 'print';
 31	 link.onload = () => {
 32	 link.media = 'all';
 33	 };
 34	 link.onerror = () => {
 35	 console.warn(`Failed to load CSS: ${href}`);
 36	 };
 37	 document.head.appendChild(link);
 38	 }, delay);
 39	 };
 40	 
 41	 // 根据网络条件调整延迟时间
 42	 const baseDelay = shouldDelay ? 5000 : 1000;
 43	 
 44	 // 示例：延迟加载字体
 45	 // loadCSS('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600&display=swap', baseDelay);
 46	 
 47	 // 示例：延迟加载图标字体
 48	 // loadCSS('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0/css/all.min.css', baseDelay + 500);
 49	 };
 50	 
 51	 // 在空闲时或页面加载完成后加载非关键资源
 52	 runWhenIdle(loadNonCriticalResources, 3000);
 53	 
 54	 // 清理函数
 55	 return () => {
 56	 performanceMonitor.stopMonitoring();
 57	 };
 58	 }, []);
 59	
 60	 return (
 61	 <>
 62	 <Head>
 63	 <meta name='viewport' content='initial-scale=1.0, width=device-width' />
 64	 {/* 预加载关键资源 */}
 65	 <link rel="preload" href="/favicon.ico" as="image" />
 66	 
 67	 {/* 性能优化提示 */}
 68	 {process.env.NODE_ENV === 'development' && (
 69	 <meta name="performance-hint" content="延迟加载已启用" />
 70	 )}
 71	 </Head>
 72	
 73	 <ChakraProvider>
 74	 <LanguageSwitcher />
 75	 <Component {...pageProps} />
 76	 <Navigation />
 77	 
 78	 {/* 延迟加载第三方脚本 */}
 79	 <ThirdPartyScripts 
 80	 analytics={{
 81	 googleAnalyticsId: process.env.NEXT_PUBLIC_GOOGLE_ANALYTICS_ID
 82	 }}
 83	 ads={{
 84	 enabled: false // 暂时关闭广告，避免影响性能
 85	 }}
 86	 social={false} // 社交脚本可以按需启用
 87	 customerService={null}
 88	 />
 89	 </ChakraProvider>
 90	 </>
 91	 );
