 37	 const { locale, t } = useLocale();
 38	
 39	 // 表格数据状态
 40	 const [tableData_member, setTableDataMember] = useState([]);
 41	 const [tableData_menke, setTableDataMenke] = useState([]);
 42	 const [tableData_qu, setTableDataQu] = useState([]);
 43	 
 44	 // 编辑状态
 45	 const [editingKey, setEditingKey] = useState('');
 46	 
 47	 // 模态框状态
 48	 const [pregnancyModal, setPregnancyModal] = useState({ isOpen: false, record: null, type: null });
 49	 const [maxAllAttributesModal, setMaxAllAttributesModal] = useState({ isOpen: false, type: null });
 50	 
 51	 // 折叠状态
 52	 const [collapseStates, setCollapseStates] = useState({
 53	 member: true,
 54	 qu: true,
 55	 menke: true
 56	 });
 57	 
 58	 // 货币和粮草状态
 59	 const [money, setMoney] = useState('');
 60	 const [yuanbao, setYuanbao] = useState('');
 61	 const [food, setFood] = useState('0');
 62	 const [vegetables, setVegetables] = useState('0');
 63	 const [meat, setMeat] = useState('0');
 64	
 65	 // 编辑相关函数
 66	 const isEditing = (record) => record.id === editingKey;
 67	
 68	 const edit = (record) => {
 69	 form.setFieldsValue({
 70	 name: '',
 71	 wen: '',
 72	 wu: '',
 73	 shang: '',
 74	 yi: '',
 75	 ...record,
 76	 });
 77	 setEditingKey(record.id);
 78	 };
 79	
 80	 const cancel = () => {
 81	 setEditingKey('');
 82	 };
 83	
 84	 // 保存成员数据
 85	 const save = async (key, memberType) => {
 86	 try {
 87	 const row = await form.validateFields();
 88	 
 89	 // 更新表格数据
 90	 let newData;
 91	 if (memberType === MEMBER_TYPES.GUEST) {
 92	 newData = [...tableData_menke];
 93	 const index = newData.findIndex((item) => key === item.id);
 94	 if (index > -1) {
 95	 newData.splice(index, 1, { ...newData[index], ...row });
 96	 setTableDataMenke(newData);
 97	 }
 98	 } else if (memberType === MEMBER_TYPES.FAMILY) {
 99	 newData = [...tableData_member];
 100	 const index = newData.findIndex((item) => key === item.id);
 101	 if (index > -1) {
 102	 newData.splice(index, 1, { ...newData[index], ...row });
 103	 setTableDataMember(newData);
 104	 }
 105	 } else if (memberType === MEMBER_TYPES.SPOUSE) {
 106	 newData = [...tableData_qu];
 107	 const index = newData.findIndex((item) => key === item.id);
 108	 if (index > -1) {
 109	 newData.splice(index, 1, { ...newData[index], ...row });
 110	 setTableDataQu(newData);
 111	 }
 112	 }
 113	 
 114	 // 更新原始数据
 115	 const rawData = JSON.parse(data.data.toString());
 116	 const updatedRawData = updateMemberInRawData(rawData, memberType, key, row);
 117	 setData({ ...data, data: Buffer.from(JSON.stringify(updatedRawData)) });
 118	 
 119	 setEditingKey('');
 120	 } catch (errInfo) {
 121	 console.log('Validate Failed:', errInfo);
 122	 }
 123	 };
 124	
 125	 // 批量操作函数
 126	 const confirmMaxAllAttributes = (type) => {
 127	 setMaxAllAttributesModal({ isOpen: true, type });
 128	 };
 129	
 130	 const executeMaxAllAttributes = () => {
 131	 const type = maxAllAttributesModal.type;
 132	 setMaxAllAttributesModal({ isOpen: false, type: null });
 133	 
 134	 const rawData = JSON.parse(data.data.toString());
 135	 const updatedRawData = maxAllMemberAttributes(rawData, type);
 136	 setData({ ...data, data: Buffer.from(JSON.stringify(updatedRawData)) });
 137	 
 138	 // 重新解析数据并更新表格
 139	 updateAllTableData(updatedRawData);
 140	 
 141	 message.success({
 142	 content: locale === 'zh' ? '全属性已升满！不要忘记保存到存档文件！' : 'All attributes maxed! Don\'t forget to save to file!',
 143	 duration: 3
 144	 });
 145	 };
 146	
 147	 const handleSetAllAge18 = (type) => {
 148	 const rawData = JSON.parse(data.data.toString());
 149	 const updatedRawData = setAllMemberAge18(rawData, type);
 150	 setData({ ...data, data: Buffer.from(JSON.stringify(updatedRawData)) });
 151	 
 152	 updateAllTableData(updatedRawData);
 153	 
 154	 message.success({
 155	 content: locale === 'zh' ? '全员年龄已设为18岁！不要忘记保存到存档文件！' : 'All ages set to 18! Don\'t forget to save to file!',
 156	 duration: 3
 157	 });
 158	 };
 159	
 160	 const handleMaxAllReputation = (type) => {
 161	 const rawData = JSON.parse(data.data.toString());
 162	 const updatedRawData = maxAllMemberReputation(rawData, type);
 163	 setData({ ...data, data: Buffer.from(JSON.stringify(updatedRawData)) });
 164	 
 165	 updateAllTableData(updatedRawData);
 166	 
 167	 message.success({
 168	 content: locale === 'zh' ? '全员声誉已升满！不要忘记保存到存档文件！' : 'All reputation maxed! Don\'t forget to save to file!',
 169	 duration: 3
 170	 });
 171	 };
 172	
 173	 const maxSingleMemberAttributes = (record, type) => {
 174	 const rawData = JSON.parse(data.data.toString());
 175	 // 这里可以复用批量操作的逻辑，但只对单个成员操作
 176	 const updatedRawData = maxAllMemberAttributes({ ...rawData, [type]: { value: [rawData[type].value.find(m => m[0] === record.id)] } }, type);
 177	 // 将更新后的成员数据放回原数据
 178	 const memberIndex = rawData[type].value.findIndex(m => m[0] === record.id);
 179	 if (memberIndex > -1) {
 180	 rawData[type].value[memberIndex] = updatedRawData[type].value[0];
 181	 }
 182	 
 183	 setData({ ...data, data: Buffer.from(JSON.stringify(rawData)) });
 184	 updateAllTableData(rawData);
 185	 
 186	 message.success({
 187	 content: locale === 'zh' ? `${record.name} 属性已升满！` : `${record.name} attributes maxed!`,
 188	 duration: 2
 189	 });
 190	 };
 191	
 192	 // 处理解除刑罚
 193	 const handleRemovePunishment = (record, memberType) => {
 194	 const rawData = JSON.parse(data.data.toString());
 195	 const updatedRawData = removePunishment(rawData, memberType, record.id);
 196	 setData({ ...data, data: Buffer.from(JSON.stringify(updatedRawData)) });
 197	 
 198	 updateAllTableData(updatedRawData);
 199	 
 200	 message.success({
 201	 content: locale === 'zh' ? `${record.name} 刑罚已解除！` : `${record.name} punishment removed!`,
 202	 duration: 2
 203	 });
 204	 };
 205	
 206	 // 怀孕相关函数
 207	 const handlePregnancy = (record, type) => {
 208	 setPregnancyModal({ isOpen: true, record, type });
 209	 };
 210	
 211	 const confirmPregnancy = () => {
 212	 const { record, type } = pregnancyModal;
 213	 const rawData = JSON.parse(data.data.toString());
 214	 const updatedRawData = setMemberPregnant(rawData, type, record.id);
 215	 setData({ ...data, data: Buffer.from(JSON.stringify(updatedRawData)) });
 216	 
 217	 setPregnancyModal({ isOpen: false, record: null, type: null });
 218	 };
 219	
 220	 // 工具函数
 221	 const updateAllTableData = (rawData) => {
 222	 const members = parseMemberData(rawData, MEMBER_TYPES.FAMILY);
 223	 const qu = parseMemberData(rawData, MEMBER_TYPES.SPOUSE);
 224	 const menke = parseMemberData(rawData, MEMBER_TYPES.GUEST);
 225	 
 226	 setTableDataMember(members);
 227	 setTableDataQu(qu);
 228	 setTableDataMenke(menke);
 229	 };
 230	
 231	 const toggleCollapse = (section) => {
 232	 setCollapseStates(prev => ({
 233	 ...prev,
 234	 [section]: !prev[section]
 235	 }));
 236	 };
 237	
 238	 // 可编辑单元格组件
 239	 const EditableCell = useCallback(({
 240	 editing,
 241	 dataIndex,
 242	 title,
 243	 record,
 244	 index,
 245	 children,
 246	 ...restProps
 247	 }) => {
 248	 return (
 249	 <td {...restProps}>
 250	 {editing ? (
 251	 <Form.Item
 252	 name={dataIndex}
 253	 style={{ margin: 0 }}
 254	 rules={[
 255	 {
 256	 required: true,
 257	 message: `请输入 ${title}!`,
 258	 },
 259	 ]}
 260	 >
 261	 <Input />
 262	 </Form.Item>
 263	 ) : (
 264	 children
 265	 )}
 266	 </td>
 267	 );
 268	 }, []);
 269	
 270	 // 初始化数据
 271	 useEffect(() => {
 272	 if (!data?.data) {
 273	 return;
 274	 }
 275	 
 276	 const rawData = JSON.parse(data.data.toString() || "{}");
 277	 
 278	 // 解析成员数据
 279	 updateAllTableData(rawData);
 280	 
 281	 // 解析货币和粮草数据
 282	 const currencyData = getCurrencyData(rawData);
 283	 setMoney(currencyData.money);
 284	 setYuanbao(currencyData.yuanbao);
 285	 
 286	 const foodData = getFoodData(rawData);
 287	 setFood(foodData.food);
 288	 setVegetables(foodData.vegetables);
 289	 setMeat(foodData.meat);
 290	 }, [data]);
 291	
 292	 return (
 293	 <>
 294	 <Modal isOpen={isOpen} onClose={onClose} size='full' mt='20'>
 295	 <ModalOverlay />
 296	 <ModalContent>
 297	 <ModalBody mt='5'>
 298	 <Box display="flex" flexDirection="column" gap={4}>
 299	 {/* 货币和粮草管理 */}
 300	 <Box display="flex" gap={4}>
 301	 <CurrencyManager
 302	 money={money}
 303	 yuanbao={yuanbao}
 304	 onUpdate={(newMoney, newYuanbao) => {
 305	 setMoney(newMoney);
 306	 setYuanbao(newYuanbao);
 307	 }}
 308	 data={data}
 309	 setData={setData}
 310	 />
 311	 <FoodManager
 312	 food={food}
 313	 vegetables={vegetables}
 314	 meat={meat}
 315	 onUpdate={(newFood, newVegetables, newMeat) => {
 316	 setFood(newFood);
 317	 setVegetables(newVegetables);
 318	 setMeat(newMeat);
 319	 }}
 320	 data={data}
 321	 setData={setData}
 322	 />
 323	 </Box>
 324	
 325	 {/* 成员表格 */}
 326	 <MemberTable
 327	 title={t.familyMembers}
 328	 memberType={MEMBER_TYPES.FAMILY}
 329	 tableData={tableData_member}
 330	 isCollapsed={!collapseStates.member}
 331	 onToggleCollapse={() => toggleCollapse('member')}
 332	 editingKey={editingKey}
 333	 isEditing={isEditing}
 334	 edit={edit}
 335	 cancel={cancel}
 336	 save={save}
 337	 handlePregnancy={handlePregnancy}
 338	 maxSingleMemberAttributes={maxSingleMemberAttributes}
 339	 onMaxAllAttributes={confirmMaxAllAttributes}
 340	 onMaxAllReputation={handleMaxAllReputation}
 341	 onSetAllAge18={handleSetAllAge18}
 342	 form={form}
 343	 EditableCell={EditableCell}
 344	 t={t}
 345	 handleRemovePunishment={handleRemovePunishment}
 346	 />
 347	
 348	 <MemberTable
 349	 title={t.familyqu}
 350	 memberType={MEMBER_TYPES.SPOUSE}
 351	 tableData={tableData_qu}
 352	 isCollapsed={!collapseStates.qu}
 353	 onToggleCollapse={() => toggleCollapse('qu')}
 354	 editingKey={editingKey}
 355	 isEditing={isEditing}
 356	 edit={edit}
 357	 cancel={cancel}
 358	 save={save}
 359	 handlePregnancy={handlePregnancy}
 360	 maxSingleMemberAttributes={maxSingleMemberAttributes}
 361	 onMaxAllAttributes={confirmMaxAllAttributes}
 362	 onMaxAllReputation={handleMaxAllReputation}
 363	 onSetAllAge18={handleSetAllAge18}
 364	 form={form}
 365	 EditableCell={EditableCell}
 366	 t={t}
 367	 handleRemovePunishment={handleRemovePunishment}
 368	 />
 369	
 370	 <MemberTable
 371	 title={t.guests}
 372	 memberType={MEMBER_TYPES.GUEST}
 373	 tableData={tableData_menke}
 374	 isCollapsed={!collapseStates.menke}
 375	 onToggleCollapse={() => toggleCollapse('menke')}
 376	 editingKey={editingKey}
 377	 isEditing={isEditing}
 378	 edit={edit}
 379	 cancel={cancel}
 380	 save={save}
 381	 handlePregnancy={handlePregnancy}
 382	 maxSingleMemberAttributes={maxSingleMemberAttributes}
 383	 onMaxAllAttributes={confirmMaxAllAttributes}
 384	 onMaxAllReputation={handleMaxAllReputation}
 385	 onSetAllAge18={handleSetAllAge18}
 386	 form={form}
 387	 EditableCell={EditableCell}
 388	 t={t}
 389	 handleRemovePunishment={handleRemovePunishment}
 390	 />
 391	 </Box>
 392	 </ModalBody>
 393	 
 394	 <ModalFooter style={{position: 'fixed', top: 0, right: 0}}>
 395	 <Button
 396	 colorScheme='orange'
 397	 isDisabled={isLoading}
 398	 onClick={async () => {
 399	 setIsLoading(true);
 400	 const isSaveSuccess = await saveData();
 401	 setIsLoading(false);
 402	 if (isSaveSuccess) onClose();
 403	 }}
 404	 >
 405	 {locale === 'zh' ? '保存到存档文件' : 'Save to File'}
 406	 </Button>
 407	 <Button
 408	 ml='3'
 409	 onClick={() => {
 410	 setData(null);
 411	 onClose();
 412	 }}
 413	 isDisabled={isLoading}
 414	 >
 415	 {locale === 'zh' ? '关闭' : 'Close'}
 416	 </Button>
 417	 </ModalFooter>
 418	 </ModalContent>
 419	 </Modal>
 420	
 421	 {/* 立即受孕确认对话框 */}
 422	 <Modal isOpen={pregnancyModal.isOpen} onClose={() => setPregnancyModal({ isOpen: false, record: null, type: null })} size="md">
 423	 <ModalOverlay />
 424	 <ModalContent>
 425	 <ModalHeader>
 426	 {locale === 'zh' ? '立即怀孕确认' : 'Immediate Pregnancy Confirmation'}
 427	 </ModalHeader>
 428	 <ModalCloseButton />
 429	 <ModalBody>
 430	 <Alert status="warning" mb={4}>
 431	 <AlertIcon />
 432	 <Box>
 433	 <AlertTitle>
 434	 {locale === 'zh' ? '警告！' : 'Warning!'}
 435	 </AlertTitle>
 436	 <AlertDescription>
 437	 {locale === 'zh' 
 438	 ? '请使用前先备份存档，以防造成无法挽回的影响！' 
 439	 : 'Please backup your save file before use to prevent irreversible effects!'
 440	 }
 441	 </AlertDescription>
 442	 </Box>
 443	 </Alert>
 444	 <Text>
 445	 {locale === 'zh' 
 446	 ? `确定要让 "${pregnancyModal.record?.name}" 立即怀孕吗？` 
 447	 : `Are you sure you want "${pregnancyModal.record?.name}" to become pregnant immediately?`
 448	 }
 449	 </Text>
 450	 </ModalBody>
 451	 <ModalFooter>
 452	 <Button 
 453	 colorScheme="red" 
 454	 mr={3} 
 455	 onClick={confirmPregnancy}
 456	 >
 457	 {locale === 'zh' ? '确认' : 'Confirm'}
 458	 </Button>
 459	 <Button onClick={() => setPregnancyModal({ isOpen: false, record: null, type: null })}>
 460	 {locale === 'zh' ? '取消' : 'Cancel'}
 461	 </Button>
 462	 </ModalFooter>
 463	 </ModalContent>
 464	 </Modal>
 465	
 466	 {/* 全属性升满确认对话框 */}
 467	 <Modal isOpen={maxAllAttributesModal.isOpen} onClose={() => setMaxAllAttributesModal({ isOpen: false, type: null })} size="md">
 468	 <ModalOverlay />
 469	 <ModalContent>
 470	 <ModalHeader>
 471	 {locale === 'zh' ? '一键升满全属性确认' : 'Max All Attributes Confirmation'}
 472	 </ModalHeader>
 473	 <ModalCloseButton />
 474	 <ModalBody>
 475	 <Alert status="warning" mb={4}>
 476	 <AlertIcon />
 477	 <Box>
 478	 <AlertTitle>
 479	 {locale === 'zh' ? '重要提醒！' : 'Important Warning!'}
 480	 </AlertTitle>
 481	 <AlertDescription>
 482	 {locale === 'zh' 
 483	 ? '如果列表中包含被贬成员，批量操作可能存在坏档风险！' 
 484	 : 'If the list contains demoted members, batch operations may pose a save corruption risk!'
 485	 }
 486	 </AlertDescription>
 487	 </Box>
 488	 </Alert>
 489	 <Text>
 490	 {locale === 'zh' 
 491	 ? '确定要对当前列表中的所有成员执行升满全属性操作吗？'
 492	 : 'Are you sure you want to max all attributes for all members in the current list?'
 493	 }
 494	 </Text>
 495	 </ModalBody>
 496	 <ModalFooter>
 497	 <Button 
 498	 colorScheme="red" 
 499	 mr={3} 
 500	 onClick={executeMaxAllAttributes}
 501	 >
 502	 {locale === 'zh' ? '确认执行' : 'Confirm'}
 503	 </Button>
 504	 <Button onClick={() => setMaxAllAttributesModal({ isOpen: false, type: null })}>
 505	 {locale === 'zh' ? '取消' : 'Cancel'}
 506	 </Button>
 507	 </ModalFooter>
 508	 </ModalContent>
 509	 </Modal>
 510	 </>
 511	 );
 512	} 
