 12	 const t = translations[locale] || translations.zh;
...
 18	
 19	 if (savedLocale && (savedLocale === 'zh' || savedLocale === 'en' || savedLocale === 'th')) {
 20	 setLocale(savedLocale);
 21	 } else {
 22	 // 检测浏览器语言
 23	 const browserLang = navigator.language.toLowerCase();
 24	 let detectedLocale = 'en'; // 默认英语
 25	 let languageName = 'English';
 26	
 27	 if (browserLang.startsWith('zh')) {
 28	 detectedLocale = 'zh';
 29	 languageName = '中文';
 30	 } else if (browserLang.startsWith('th')) {
 31	 detectedLocale = 'th';
 32	 languageName = 'ไทย';
 33	 }
 34	
 35	 setLocale('en'); // 先设置为英语显示提示
 36	
 37	 // 如果检测到的语言不是英语且没有显示过提示，则显示语言切换提示
 38	 if (detectedLocale !== 'en' && !hasShownPrompt) {
 39	 setDetectedLanguage(languageName);
 40	 setSuggestedLocale(detectedLocale);
 41	 setShowLanguagePrompt(true);
 42	 } else {
 43	 setLocale(detectedLocale);
 44	 localStorage.setItem('locale', detectedLocale);
 45	 }
 46	 }
 47	 }, []);
 48	
 49	 const switchLanguage = () => {
 50	 // 三语言循环切换：中文 -> 英文 -> 泰文 -> 中文
 51	 let newLocale;
 52	 if (locale === 'zh') {
 53	 newLocale = 'en';
 54	 } else if (locale === 'en') {
 55	 newLocale = 'th';
 56	 } else {
 57	 newLocale = 'zh';
 58	 }
 59	
 60	 setLocale(newLocale);
 61	 localStorage.setItem('locale', newLocale);
 62	
 63	 // 触发自定义事件通知其他组件语言已更改
 64	 window.dispatchEvent(new CustomEvent('localeChange', { detail: newLocale }));
 65	 };
 66	
 67	 const handleLanguagePromptSwitch = () => {
 68	 setLocale(suggestedLocale);
 69	 localStorage.setItem('locale', suggestedLocale);
 70	 localStorage.setItem('languagePromptShown', 'true');
 71	 setShowLanguagePrompt(false);
 72	
 73	 // 触发自定义事件通知其他组件语言已更改
 74	 window.dispatchEvent(new CustomEvent('localeChange', { detail: suggestedLocale }));
 75	 };
 76	
 77	 const handleLanguagePromptKeep = () => {
 78	 localStorage.setItem('languagePromptShown', 'true');
 79	 setShowLanguagePrompt(false);
 80	 };
 81	
 82	 // 获取按钮显示文本和提示
 83	 const getButtonText = () => {
 84	 if (locale === 'zh') return 'A';
 85	 if (locale === 'en') return 'ไทย';
 86	 return '中';
 87	 };
 88	
 89	 const getButtonTitle = () => {
 90	 if (locale === 'zh') return 'Switch to English';
 91	 if (locale === 'en') return 'เปลี่ยนเป็นภาษาไทย';
 92	 return '切换到中文';
 93	 };
 94	
 95	 return (
 96	 <>
 97	 {/* 语言检测提示 */}
 98	 {showLanguagePrompt && (
 99	 <Box
 100	 position="fixed"
 101	 top="4"
 102	 left="50%"
 103	 transform="translateX(-50%)"
 104	 zIndex="1001"
 105	 maxWidth="400px"
 106	 width="90%"
 107	 >
 108	 <Alert status="info" borderRadius="md" boxShadow="lg">
 109	 <AlertIcon />
 110	 <Box>
 111	 <AlertTitle fontSize="sm">
 112	 {t.languageDetection.detected} {detectedLanguage}
 113	 </AlertTitle>
 114	 <AlertDescription fontSize="sm" mt={1}>
 115	 {t.languageDetection.switchPrompt}
 116	 </AlertDescription>
 117	 <Box mt={3} display="flex" gap={2}>
 118	 <Button
 119	 size="xs"
 120	 colorScheme="blue"
 121	 onClick={handleLanguagePromptSwitch}
 122	 >
 123	 {t.languageDetection.switch}
 124	 </Button>
 125	 <Button
 126	 size="xs"
 127	 variant="outline"
 128	 onClick={handleLanguagePromptKeep}
 129	 >
 130	 {t.languageDetection.keep}
 131	 </Button>
 132	 </Box>
 133	 </Box>
 134	 </Alert>
 135	 </Box>
 136	 )}
