 537	
 538	/**
 539	 * 将修改后的物品清单更新回存档数据
 540	 */
 541	export const updateInventoryItems = (saveData, items) => {
 542	 if (!saveData?.root?.properties) {
 543	 throw new Error('无效的存档数据结构');
 544	 }
 545	
 546	 const updatedSaveData = JSON.parse(JSON.stringify(saveData)); // 深拷贝
 547	 const properties = updatedSaveData.root.properties;
 548	 
 549	 // 更新所有 InventoryItems_ 属性
 550	 Object.entries(properties).forEach(([key, value]) => {
 551	 if (key.startsWith('InventoryItems_') && value && typeof value === 'object' && 'Map' in value) {
 552	 value.Map = items;
 553	 console.log(` 更新了 ${key} 中的物品数据`);
 554	 }
 555	 });
 556	 
 557	 return updatedSaveData;
 558	};
 559	
 560	/**
 561	 * 查找特定物品
 562	 */
 563	export const findInventoryItem = (items, itemName) => {
 564	 return items.find(item => item.key?.Name === itemName);
 565	};
